{
  description = "Architecture Graph DB - DDL分離とDQL充実のためのflake";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";
    flake-utils.url = "github:numtide/flake-utils";
  };

  outputs = { self, nixpkgs, flake-utils }:
    flake-utils.lib.eachDefaultSystem (system:
      let
        pkgs = nixpkgs.legacyPackages.${system};

        pythonEnv = pkgs.python311.withPackages (ps: with ps; [
          # 基本的なパッケージ
          typing-extensions
        ]);
      in
      {
        devShells.default = pkgs.mkShell {
          buildInputs = with pkgs; [
            pythonEnv
            # 開発ツール
            ruff
            black
            mypy
          ];

          shellHook = ''
            echo "Architecture Graph DB Development Environment"
            echo "============================================"
            echo "責務: アーキテクチャをgraph dbで管理するためのスキーマ定義とDQL充実"
            echo ""
            echo "利用可能なコマンド:"
            echo "  python infrastructure/schema_manager.py apply  - スキーマ生成"
            echo "  python infrastructure/query_runner.py execute <query> - クエリ実行"
            echo ""
          '';
        };

        # スキーマ管理アプリケーション
        apps.schema = {
          type = "app";
          program = "${pkgs.writeShellScriptBin "architecture-schema" ''
            ${pythonEnv}/bin/python ${self}/infrastructure/schema_manager.py "$@"
          ''}/bin/architecture-schema";
        };

        # クエリ実行アプリケーション  
        apps.query = {
          type = "app";
          program = "${pkgs.writeShellScriptBin "architecture-query" ''
            ${pythonEnv}/bin/python ${self}/infrastructure/query_runner.py "$@"
          ''}/bin/architecture-query";
        };

        # デフォルトアプリケーション
        apps.default = self.apps.${system}.schema;
      });
}

