-- Add requirement mapping for integration with requirement/graph
-- Date: 2025-08-03

-- Mapping table to connect implementations with requirements
CREATE NODE TABLE RequirementMapping (
    id STRING PRIMARY KEY,
    requirement_id STRING,      -- ID from requirement/graph
    implementation_id STRING,   -- ID from ImplementationEntity
    mapping_type STRING        -- implements/tests/documents
);

-- Link implementations to requirement mappings
CREATE REL TABLE MAPS_TO_REQUIREMENT (
    FROM ImplementationEntity TO RequirementMapping
);

-- Implementation depends on specific responsibilities
CREATE REL TABLE DEPENDS_ON_RESPONSIBILITY (
    FROM ImplementationEntity TO Responsibility
);

