#!/usr/bin/env bash
# setup-mcp-link.sh - Create symlink to shared .mcp.json

set -euo pipefail

# Master .mcp.json location
MASTER_MCP="/home/nixos/.config/claude/shared.mcp.json"

# Create master .mcp.json if it doesn't exist
if [[ ! -f "$MASTER_MCP" ]]; then
    mkdir -p "$(dirname "$MASTER_MCP")"
    cat > "$MASTER_MCP" << 'EOF'
{
  "mcpServers": {
    "github": {
      "type": "stdio",
      "command": "npx",
      "args": ["-y", "@modelcontextprotocol/server-github"],
      "env": {
        "GITHUB_PERSONAL_ACCESS_TOKEN": "${GITHUB_TOKEN}"
      }
    },
    "filesystem": {
      "type": "stdio",
      "command": "npx",
      "args": ["-y", "@modelcontextprotocol/server-filesystem", "${HOME}"]
    }
  }
}
EOF
    echo "Created master .mcp.json at $MASTER_MCP"
fi

# Create symlink in current directory
if [[ ! -e .mcp.json ]]; then
    ln -s "$MASTER_MCP" .mcp.json
    echo "Created symlink: .mcp.json -> $MASTER_MCP"
else
    echo ".mcp.json already exists in current directory"
fi

