import { defineApp } from "rwsdk/worker";
import { route, render } from "rwsdk/router";
import { Document } from "@/app/Document";
import { Home } from "@/app/pages/Home";
import { setCommonHeaders } from "@/app/headers";
import { logInfo } from "@/utils/logger";

export type AppContext = {};

export default defineApp([
  setCommonHeaders(),
  async ({ request, env }) => {
    // Minimal logging for POC
    const url = new URL(request.url);
    logInfo(
      "server_request",
      `[POC] ${request.method} ${url.pathname}`,
      {
        method: request.method,
        path: url.pathname,
      }
    );

    // R2 file serving endpoint
    if (url.pathname.startsWith('/api/r2/')) {
      const objectKey = url.pathname.replace('/api/r2/', '');

      // @ts-ignore - R2_BUCKET is defined in wrangler.jsonc
      const r2Bucket = env.R2_BUCKET;
      if (!r2Bucket) {
        return new Response('R2 bucket not configured', { status: 500 });
      }

      try {
        const object = await r2Bucket.get(objectKey);
        if (!object) {
          return new Response('File not found', { status: 404 });
        }

        // Set appropriate headers for WASM files
        const headers = new Headers();
        headers.set('Content-Type', objectKey.endsWith('.wasm') 
          ? 'application/wasm' 
          : objectKey.endsWith('.js') || objectKey.endsWith('.mjs')
          ? 'application/javascript'
          : 'application/octet-stream');
        headers.set('Cache-Control', 'public, max-age=31536000'); // 1 year cache
        headers.set('Access-Control-Allow-Origin', '*'); // CORS for WASM

        return new Response(object.body, {
          status: 200,
          headers
        });
      } catch (error) {
        logInfo('r2_error', `Failed to fetch from R2: ${error}`);
        return new Response('Error fetching from R2', { status: 500 });
      }
    }
  },
  render(Document, [
    route("/", [
      () => {
        logInfo(
          'home_route',
          'Serving DuckDB WASM POC'
        );
      },
      Home,
    ]),
  ]),
]);

