{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Module Definition Schema",
  "description": "構造化READMEのモジュール定義スキーマ",
  "type": "object",
  "required": ["id", "purpose", "value_proposition", "responsibilities"],
  "properties": {
    "id": {
      "type": "string",
      "description": "モジュールの一意識別子",
      "pattern": "^[a-z0-9-]+$"
    },
    "purpose": {
      "type": "string",
      "description": "モジュールの目的（1文で表現）",
      "minLength": 10,
      "maxLength": 200
    },
    "value_proposition": {
      "type": "array",
      "description": "提供する価値のリスト",
      "minItems": 1,
      "items": {
        "type": "string",
        "minLength": 5
      }
    },
    "responsibilities": {
      "type": "array",
      "description": "モジュールの責務",
      "minItems": 1,
      "items": {
        "type": "object",
        "required": ["name", "description"],
        "properties": {
          "name": {
            "type": "string",
            "description": "責務の名前"
          },
          "description": {
            "type": "string",
            "description": "責務の詳細説明"
          }
        }
      }
    },
    "dependencies": {
      "type": "array",
      "description": "依存する他モジュールのID",
      "items": {
        "type": "string",
        "pattern": "^[a-z0-9-]+$"
      }
    },
    "constraints": {
      "type": "array",
      "description": "制約条件",
      "items": {
        "type": "object",
        "required": ["type", "rule"],
        "properties": {
          "type": {
            "type": "string",
            "enum": ["technical", "business", "security", "performance"],
            "description": "制約の種類"
          },
          "rule": {
            "type": "string",
            "description": "制約ルールの記述"
          },
          "severity": {
            "type": "string",
            "enum": ["must", "should", "may"],
            "default": "must",
            "description": "制約の重要度"
          }
        }
      }
    },
    "interfaces": {
      "type": "object",
      "description": "公開インターフェース",
      "properties": {
        "inputs": {
          "type": "array",
          "description": "入力仕様",
          "items": {
            "type": "object",
            "required": ["name", "type"],
            "properties": {
              "name": {
                "type": "string"
              },
              "type": {
                "type": "string"
              },
              "required": {
                "type": "boolean",
                "default": true
              },
              "description": {
                "type": "string"
              }
            }
          }
        },
        "outputs": {
          "type": "array",
          "description": "出力仕様",
          "items": {
            "type": "object",
            "required": ["name", "type"],
            "properties": {
              "name": {
                "type": "string"
              },
              "type": {
                "type": "string"
              },
              "description": {
                "type": "string"
              }
            }
          }
        }
      }
    },
    "decisions": {
      "type": "array",
      "description": "設計上の判断基準",
      "items": {
        "type": "object",
        "required": ["context", "decision", "rationale"],
        "properties": {
          "context": {
            "type": "string",
            "description": "判断が必要な文脈"
          },
          "decision": {
            "type": "string",
            "description": "下された決定"
          },
          "rationale": {
            "type": "string",
            "description": "決定の根拠"
          },
          "alternatives": {
            "type": "array",
            "description": "検討された代替案",
            "items": {
              "type": "string"
            }
          }
        }
      }
    },
    "metadata": {
      "type": "object",
      "description": "メタデータ",
      "properties": {
        "version": {
          "type": "string",
          "pattern": "^\\d+\\.\\d+\\.\\d+$"
        },
        "created_at": {
          "type": "string",
          "format": "date-time"
        },
        "updated_at": {
          "type": "string",
          "format": "date-time"
        },
        "author": {
          "type": "string"
        },
        "tags": {
          "type": "array",
          "items": {
            "type": "string"
          }
        }
      }
    }
  }
}

