input.new_string:
{
  description = "Qwen-Code CLI tool";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";
  };

  outputs = { self, nixpkgs }:
    let
      supportedSystems = [ "x86_64-linux" "aarch64-linux" "x86_64-darwin" "aarch64-darwin" ];
      forAllSystems = nixpkgs.lib.genAttrs supportedSystems;
    in
    {
      devShells = forAllSystems (system:
        let
          pkgs = nixpkgs.legacyPackages.${system};
          qwen-code-wrapper = pkgs.writeShellScriptBin "qwen-code" ''
            # Qwen-Code CLI wrapper for Nix
            set -euo pipefail

            # Check if qwen-code is already installed via npm
            if command -v qwen-code &> /dev/null && [[ "$(command -v qwen-code)" != "${placeholder "out"}/bin/qwen-code" ]]; then
                # If installed (and not this wrapper), run it directly
                exec "$(command -v qwen-code)" "$@"
            else
                # If not installed, provide installation instructions
                echo "Qwen-Code CLI is not installed."
                echo ""
                echo "To install it in this shell, run:"
                echo "  npm install -g @qwen-code/qwen-code@latest"
                echo ""
                echo "Then you can use: qwen-code [arguments]"
                exit 1
            fi
          '';
        in
        {
          default = pkgs.mkShell {
            buildInputs = with pkgs; [
              nodejs_20
              nodePackages.npm
              qwen-code-wrapper
            ];

            shellHook = ''
              echo "Qwen-Code development shell"
              echo "Node.js: $(node --version)"
              echo "npm: $(npm --version)"
              echo ""
              echo "To install qwen-code: npm install -g @qwen-code/qwen-code@latest"
              echo "To run the wrapper: qwen-code"
            '';
          };
        });
    };
}

