    .version('1.0.0')
    .description('Example flake contract')
    .input('nixpkgs', 'flake:nixpkgs')
    .output('packages', { type: 'package' })
    .output('devShell', { type: 'devShell' })
    .capabilities(['deno', 'typescript', 'glue'])
