{
  description = "Flake contract system - TypeScript-based contract definitions for Nix flakes";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";
    flake-utils.url = "github:numtide/flake-utils";
  };

  outputs = { self, nixpkgs, flake-utils }:
    flake-utils.lib.eachDefaultSystem (system:
      let
        pkgs = nixpkgs.legacyPackages.${system};
      in
      {
        devShells.default = pkgs.mkShell {
          buildInputs = with pkgs; [
            deno
            nodePackages.typescript
            nodePackages.typescript-language-server
          ];

          shellHook = ''
            echo "Flake Contract System Development Environment"
            echo "============================================"
            echo "Deno: $(deno --version | head -n1)"
            echo ""
            echo "Available commands:"
            echo "  deno check    - Type check all TypeScript files"
            echo "  deno test     - Run tests"
            echo "  deno fmt      - Format code"
            echo "  deno lint     - Lint code"
            echo ""
          '';
        };

        # Flake contract metadata (consumed by glue system)
        contract = {
          version = "0.1.0";
          interface = {
            inputs = {
              nixpkgs = "flake:nixpkgs";
              flake-utils = "flake:flake-utils";
            };
            outputs = {
              devShell = "derivation";
              contract = "attrset";
            };
          };
          capabilities = [ "deno" "typescript" "glue" ];
        };

        # Package definition for the contract system
        packages.default = pkgs.stdenv.mkDerivation {
          pname = "flake-contract";
          version = "0.1.0";
          src = ./.;

          buildInputs = [ pkgs.deno ];

          buildPhase = ''
            deno cache src/glue/flake-glue.ts
          '';

          installPhase = ''
            mkdir -p $out/bin $out/lib
            cp -r src $out/lib/
            cat > $out/bin/flake-contract <<EOF
            #!/usr/bin/env bash
            exec ${pkgs.deno}/bin/deno run --allow-read --allow-env $out/lib/src/glue/flake-glue.ts "\$@"
            EOF
            chmod +x $out/bin/flake-contract
          '';
        };
      });
}

