{
  description = "Level 3 Consumer - Uses producer command via buildInputs";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";
    flake-utils.url = "github:numtide/flake-utils";
    # In real usage: producer.url = "github:org/level3-producer";
    # For demo, we'll use local path
    producer.url = "path:../level3-producer";
  };

  outputs = { self, nixpkgs, flake-utils, producer }:
    flake-utils.lib.eachDefaultSystem (system:
      let
        pkgs = nixpkgs.legacyPackages.${system};
      in
      {
        packages.default = pkgs.stdenv.mkDerivation {
          pname = "level3-consumer";
          version = "1.0.0";

          # Level 3: Producer command automatically in PATH
          buildInputs = [
            producer.packages.${system}.default
            pkgs.bun
            pkgs.jq
          ];

          src = ./.;

          buildPhase = ''
            # Create TypeScript consumer
            cat > consumer.ts << 'EOF'
            import { spawn } from 'child_process';
            import { promisify } from 'util';

            const execCommand = async (command: string[], input?: string): Promise<string> => {
              return new Promise((resolve, reject) => {
                // Level 3: Just use command name, no path needed!
                const proc = spawn(command[0], command.slice(1));
                let output = '';
                let error = '';

                proc.stdout.on('data', (data) => output += data);
                proc.stderr.on('data', (data) => error += data);

                if (input) {
                  proc.stdin.write(input);
                  proc.stdin.end();
                }

                proc.on('close', (code) => {
                  if (code === 0) resolve(output);
                  else reject(new Error(error || `Exit code: ${code}`));
                });
              });
            };

            async function main() {
              const testData = {
                items: [
                  { id: 1, name: "Item 1" },
                  { id: 2, name: "Item 2" },
                  { id: 3, name: "Item 3" }
                ]
              };

              try {
                // Level 3: No path needed, just command name!
                const result = await execCommand(
                  ['data-processor', '--process'],
                  JSON.stringify(testData)
                );

                const parsed = JSON.parse(result);
                console.log("Processing Report:");
                console.log("==================");
                console.log(`Items processed: ${parsed.processed}`);
                console.log(`Items failed: ${parsed.failed}`);
                console.log("Output:", JSON.stringify(parsed.output, null, 2));
              } catch (error) {
                console.error("Error:", error);
                process.exit(1);
              }
            }

            main();
            EOF

            # Bundle it
            bun build consumer.ts --outfile dist/consumer.js
          '';

          installPhase = ''
            mkdir -p $out/bin

            # Create runner script
            cat > $out/bin/level3-consumer << EOF
            #!/usr/bin/env bash
            # Level 3: data-processor is in PATH via buildInputs
            exec ${pkgs.bun}/bin/bun run $out/lib/consumer.js "\$@"
            EOF
            chmod +x $out/bin/level3-consumer

            # Install the bundle
            mkdir -p $out/lib
            cp dist/consumer.js $out/lib/
          '';
        };

        # Level 3 contract metadata
        contract = {
          version = "1.0.0";
          type = "consumer";
          commands = {
            processor = {
              command = "data-processor";  # Same name, no path
              version = "1.0.0";
              capabilities = [ "json-processing" ];
            };
          };
          interface = {
            inputs = {
              rawData = {
                type = "array";
                schema = "items";
              };
            };
            outputs = {
              report = {
                type = "json";
                schema = "summary";
              };
            };
          };
          capabilities = [ "path-managed" "pure-contract" "data-consumer" ];
        };
      });
}

