/**
 * Pure Flake Glue - No file paths, only flake references
 * 
 * This implementation follows Nix's purity principles:
 * - No direct file system access
 * - All references through flake URLs
 * - Path resolution delegated to Nix
 */

import { z } from 'zod';

/**
 * Flake Reference - Pure reference without paths
 */
export const FlakeReferenceSchema = z.object({
  url: z.string().describe('Flake URL (e.g., github:owner/repo, flake:nixpkgs)'),
  input: z.string().optional().describe('Input name in parent flake'),
  output: z.string().optional().describe('Output attribute (e.g., packages.x86_64-linux.default)'),
});

/**
 * Pure Contract - No path information
 */
export const PureContractSchema = z.object({
  flake: FlakeReferenceSchema,
  commands: z.record(z.string(), z.object({
    name: z.string(),
    version: z.string(),
    capabilities: z.array(z.string()),
  })),
  interface: z.object({
    inputs: z.record(z.string(), z.any()),
    outputs: z.record(z.string(), z.any()),
  }),
});

/**
 * Pure Flake Glue
 * 
 * Orchestrates flake interactions without touching the file system
 */
export class PureFlakeGlue {
  /**
   * Validate contract compatibility without path access
   */
  validateCompatibility(
    producer: z.infer<typeof PureContractSchema>,
    consumer: z.infer<typeof PureContractSchema>
  ): { valid: boolean; errors?: string[] } {
    const errors: string[] = [];

    // Check command compatibility
    for (const [cmdKey, consumerCmd] of Object.entries(consumer.commands)) {
      const producerCmd = producer.commands[cmdKey];
      if (!producerCmd) {
        errors.push(`Command '${consumerCmd.name}' not provided by producer`);
      } else if (producerCmd.name !== consumerCmd.name) {
        errors.push(`Command name mismatch: expected '${consumerCmd.name}', got '${producerCmd.name}'`);
      }
    }

    // Validate interface compatibility (simplified)
    const producerOutputKeys = Object.keys(producer.interface.outputs);
    const consumerInputKeys = Object.keys(consumer.interface.inputs);

    for (const inputKey of consumerInputKeys) {
      if (!producerOutputKeys.includes(inputKey)) {
        errors.push(`Consumer requires input '${inputKey}' not provided by producer`);
      }
    }

    if (errors.length > 0) {
      return { valid: false, errors };
    }

    return { valid: true };
  }

  /**
   * Compose flakes without file system access
   * Returns a new flake reference that combines both
   */
  composeFlakes(
    producer: z.infer<typeof PureContractSchema>,
    consumer: z.infer<typeof PureContractSchema>
  ): z.infer<typeof FlakeReferenceSchema> {
    // Pure composition - returns a virtual flake reference
    // Actual composition happens in Nix evaluation
    return {
      url: `composite://${producer.flake.url}+${consumer.flake.url}`,
      output: 'packages.default',
    };
  }

  /**
   * Generate Nix expression for flake evaluation
   * This is pure data - no file system access
   */
  generateNixExpression(
    flakeRef: z.infer<typeof FlakeReferenceSchema>
  ): string {
    return `
      let
        flake = builtins.getFlake "${flakeRef.url}";
        output = ${flakeRef.output ? `flake.${flakeRef.output}` : 'flake'};
      in output
    `;
  }
}

// Type exports
export type FlakeReference = z.infer<typeof FlakeReferenceSchema>;
export type PureContract = z.infer<typeof PureContractSchema>;

