/**
 * Nix Bridge - Pure interface to Nix evaluator
 * 
 * All path resolution and file system access happens in Nix,
 * not in TypeScript. This bridge only passes pure data.
 */

import { spawn } from 'child_process';
import { promisify } from 'util';
import { FlakeReference } from './pure-flake-glue';

const execAsync = promisify(require('child_process').exec);

/**
 * Nix Evaluation Result
 */
export interface NixEvalResult {
  success: boolean;
  output?: any;
  error?: string;
  derivation?: string;
}

/**
 * Pure Nix Bridge
 * 
 * Delegates all evaluation to Nix without touching the file system
 */
export class NixBridge {
  /**
   * Evaluate a flake reference
   * No file paths - only flake URLs
   */
  async evaluateFlake(flakeRef: FlakeReference): Promise<NixEvalResult> {
    try {
      const nixExpr = this.buildNixExpression(flakeRef);

      // Use nix eval with --json for structured output
      const command = `nix eval --json --expr '${nixExpr}'`;
      const { stdout, stderr } = await execAsync(command);

      if (stderr) {
        return { success: false, error: stderr };
      }

      return {
        success: true,
        output: JSON.parse(stdout),
      };
    } catch (error) {
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error',
      };
    }
  }

  /**
   * Build a flake output
   * Returns store path without accessing file system
   */
  async buildFlake(flakeRef: FlakeReference): Promise<NixEvalResult> {
    try {
      const flakeUrl = this.constructFlakeUrl(flakeRef);

      // Use nix build with --no-link to avoid creating symlinks
      const command = `nix build "${flakeUrl}" --no-link --print-out-paths`;
      const { stdout, stderr } = await execAsync(command);

      if (stderr && !stderr.includes('warning')) {
        return { success: false, error: stderr };
      }

      // Store path is returned, but we don't access it directly
      const storePath = stdout.trim();

      return {
        success: true,
        derivation: storePath,
      };
    } catch (error) {
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error',
      };
    }
  }

  /**
   * Get flake metadata without file access
   */
  async getFlakeMetadata(flakeRef: FlakeReference): Promise<NixEvalResult> {
    try {
      const flakeUrl = flakeRef.url;

      // Use nix flake metadata for pure information
      const command = `nix flake metadata "${flakeUrl}" --json`;
      const { stdout, stderr } = await execAsync(command);

      if (stderr && !stderr.includes('warning')) {
        return { success: false, error: stderr };
      }

      return {
        success: true,
        output: JSON.parse(stdout),
      };
    } catch (error) {
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error',
      };
    }
  }

  /**
   * List flake outputs without file access
   */
  async listFlakeOutputs(flakeRef: FlakeReference): Promise<NixEvalResult> {
    try {
      const flakeUrl = flakeRef.url;

      // Use nix flake show for output structure
      const command = `nix flake show "${flakeUrl}" --json`;
      const { stdout, stderr } = await execAsync(command);

      if (stderr && !stderr.includes('warning')) {
        return { success: false, error: stderr };
      }

      return {
        success: true,
        output: JSON.parse(stdout),
      };
    } catch (error) {
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error',
      };
    }
  }

  /**
   * Build pure Nix expression from flake reference
   */
  private buildNixExpression(flakeRef: FlakeReference): string {
    if (flakeRef.output) {
      return `(builtins.getFlake "${flakeRef.url}").${flakeRef.output}`;
    }
    return `builtins.getFlake "${flakeRef.url}"`;
  }

  /**
   * Construct full flake URL with output
   */
  private constructFlakeUrl(flakeRef: FlakeReference): string {
    if (flakeRef.output) {
      return `${flakeRef.url}#${flakeRef.output}`;
    }
    return flakeRef.url;
  }

  /**
   * Compose multiple flakes into a new flake
   * Returns a Nix expression that creates a composite flake
   */
  generateCompositeFlake(
    inputs: Record<string, FlakeReference>,
    outputs: string
  ): string {
    const inputsNix = Object.entries(inputs)
      .map(([name, ref]) => `    ${name}.url = "${ref.url}";`)
      .join('\n');

    return `
{
  inputs = {
${inputsNix}
  };

  outputs = { self, ... }@inputs: {
    ${outputs}
  };
}
    `.trim();
  }
}

// Export singleton instance
export const nixBridge = new NixBridge();

