/**
 * Test Pure Implementation
 * 
 * Validates that the pure implementation works without file paths
 */

import { PureFlakeGlue, PureContractSchema } from './pure-flake-glue';
import { nixBridge } from './nix-bridge';
import { FlakeReferenceBuilder, FlakeDependencyResolver, SystemArch, FlakeOutputType } from './flake-reference';
import { z } from 'zod';

async function testPureImplementation() {
  console.log('=== Pure Implementation Test ===\n');

  // Test 1: Create pure flake references
  console.log('Test 1: Creating pure flake references');
  const nixpkgsRef = FlakeReferenceBuilder.registry('nixpkgs');
  const customRef = FlakeReferenceBuilder.github('myorg', 'myflake', { ref: 'main' });

  console.log('Nixpkgs URL:', FlakeReferenceBuilder.toUrl(nixpkgsRef));
  console.log('Custom URL:', FlakeReferenceBuilder.toUrl(customRef));
  console.log('✅ Flake references created without file paths\n');

  // Test 2: Pure contract validation
  console.log('Test 2: Pure contract validation');

  const producerContract: z.infer<typeof PureContractSchema> = {
    flake: {
      url: 'github:myorg/producer',
      output: 'packages.x86_64-linux.default',
    },
    commands: {
      processor: {
        name: 'data-processor',
        version: '1.0.0',
        capabilities: ['json', 'streaming'],
      },
    },
    interface: {
      inputs: { data: {} },
      outputs: { result: {} },
    },
  };

  const consumerContract: z.infer<typeof PureContractSchema> = {
    flake: {
      url: 'github:myorg/consumer',
      output: 'packages.x86_64-linux.default',
    },
    commands: {
      processor: {
        name: 'data-processor',
        version: '1.0.0',
        capabilities: ['json'],
      },
    },
    interface: {
      inputs: { result: {} },
      outputs: { report: {} },
    },
  };

  const glue = new PureFlakeGlue();
  const validation = glue.validateCompatibility(producerContract, consumerContract);
  console.log('Validation result:', validation);
  console.log('✅ Contracts validated without file system access\n');

  // Test 3: Dependency graph generation
  console.log('Test 3: Dependency graph generation');

  const resolver = new FlakeDependencyResolver();
  resolver.addFlake('producer', customRef, [
    {
      flake: customRef,
      outputType: FlakeOutputType.PACKAGES,
      system: SystemArch.X86_64_LINUX,
      attribute: 'default',
    },
  ]);

  resolver.addFlake('nixpkgs', nixpkgsRef);
  resolver.addDependency('producer', 'nixpkgs', 'nixpkgs');

  const generatedFlake = resolver.generateFlake();
  console.log('Generated flake (first 5 lines):');
  console.log(generatedFlake.split('\n').slice(0, 5).join('\n'));
  console.log('✅ Dependency graph generated as pure data\n');

  // Test 4: Nix bridge (metadata only, no builds)
  console.log('Test 4: Nix bridge metadata retrieval');

  try {
    // Only test with nixpkgs which is always available
    const metadata = await nixBridge.getFlakeMetadata({ url: 'flake:nixpkgs' });
    if (metadata.success) {
      console.log('Nixpkgs metadata retrieved:');
      console.log('- Description:', metadata.output?.description || 'N/A');
      console.log('✅ Nix bridge works without file access');
    } else {
      console.log('⚠️ Nix metadata retrieval skipped (requires network)');
    }
  } catch (e) {
    console.log('⚠️ Nix bridge test skipped (Nix not available in test environment)');
  }

  console.log('\n=== All Pure Tests Completed ===');
  console.log('Summary: Implementation is pure - no file paths in TypeScript! 🎉');
}

// Run tests
testPureImplementation().catch(console.error);

