/**
 * Flake Reference - Type-safe flake references without file paths
 * 
 * This module provides a pure abstraction over Nix flakes,
 * ensuring no file system paths leak into TypeScript code.
 */

import { z } from 'zod';

/**
 * Flake URL Types
 * Pure references that Nix understands
 */
export enum FlakeUrlType {
  GITHUB = 'github',
  GIT = 'git',
  PATH = 'path',  // Only for Nix evaluation, not TypeScript access
  FLAKE = 'flake',
  TARBALL = 'tarball',
  FILE = 'file',  // Only for Nix evaluation, not TypeScript access
}

/**
 * System Architecture
 */
export enum SystemArch {
  X86_64_LINUX = 'x86_64-linux',
  AARCH64_LINUX = 'aarch64-linux',
  X86_64_DARWIN = 'x86_64-darwin',
  AARCH64_DARWIN = 'aarch64-darwin',
}

/**
 * Flake Output Type
 */
export enum FlakeOutputType {
  PACKAGES = 'packages',
  APPS = 'apps',
  DEVSHELLS = 'devShells',
  OVERLAYS = 'overlays',
  NIXOS_MODULES = 'nixosModules',
  NIXOS_CONFIGURATIONS = 'nixosConfigurations',
  HYDRA_JOBS = 'hydraJobs',
  TEMPLATES = 'templates',
}

/**
 * GitHub Flake Reference
 */
export const GitHubFlakeSchema = z.object({
  type: z.literal(FlakeUrlType.GITHUB),
  owner: z.string(),
  repo: z.string(),
  ref: z.string().optional(),
  rev: z.string().optional(),
});

/**
 * Generic Flake Reference
 */
export const GenericFlakeSchema = z.object({
  type: z.enum([
    FlakeUrlType.GIT,
    FlakeUrlType.FLAKE,
    FlakeUrlType.TARBALL,
  ]),
  url: z.string(),
  ref: z.string().optional(),
});

/**
 * Flake Output Reference
 */
export const FlakeOutputRefSchema = z.object({
  flake: z.union([GitHubFlakeSchema, GenericFlakeSchema]),
  outputType: z.nativeEnum(FlakeOutputType),
  system: z.nativeEnum(SystemArch).optional(),
  attribute: z.string().optional(),
});

/**
 * Flake Reference Builder
 * 
 * Constructs type-safe flake references
 */
export class FlakeReferenceBuilder {
  /**
   * Create a GitHub flake reference
   */
  static github(
    owner: string,
    repo: string,
    options?: { ref?: string; rev?: string }
  ): z.infer<typeof GitHubFlakeSchema> {
    return {
      type: FlakeUrlType.GITHUB,
      owner,
      repo,
      ...options,
    };
  }

  /**
   * Create a flake registry reference
   */
  static registry(name: string): z.infer<typeof GenericFlakeSchema> {
    return {
      type: FlakeUrlType.FLAKE,
      url: name,
    };
  }

  /**
   * Create a git flake reference
   */
  static git(url: string, ref?: string): z.infer<typeof GenericFlakeSchema> {
    return {
      type: FlakeUrlType.GIT,
      url,
      ref,
    };
  }

  /**
   * Build flake URL string for Nix
   */
  static toUrl(flake: z.infer<typeof GitHubFlakeSchema> | z.infer<typeof GenericFlakeSchema>): string {
    if (flake.type === FlakeUrlType.GITHUB) {
      const githubFlake = flake as z.infer<typeof GitHubFlakeSchema>;
      let url = `github:${githubFlake.owner}/${githubFlake.repo}`;
      if (githubFlake.ref) {
        url += `/${githubFlake.ref}`;
      }
      if (githubFlake.rev) {
        url += `?rev=${githubFlake.rev}`;
      }
      return url;
    }

    const genericFlake = flake as z.infer<typeof GenericFlakeSchema>;
    if (genericFlake.type === FlakeUrlType.FLAKE) {
      return `flake:${genericFlake.url}`;
    }

    let url = `${genericFlake.type}:${genericFlake.url}`;
    if (genericFlake.ref) {
      url += `?ref=${genericFlake.ref}`;
    }
    return url;
  }

  /**
   * Build output reference string for Nix
   */
  static toOutputRef(output: z.infer<typeof FlakeOutputRefSchema>): string {
    const baseUrl = this.toUrl(output.flake);
    const parts = [output.outputType];

    if (output.system) {
      parts.push(output.system);
    }

    if (output.attribute) {
      parts.push(output.attribute);
    }

    return `${baseUrl}#${parts.join('.')}`;
  }
}

/**
 * Flake Dependency Graph
 * 
 * Pure representation of flake dependencies
 */
export interface FlakeDependencyGraph {
  nodes: Map<string, FlakeNode>;
  edges: Array<FlakeEdge>;
}

export interface FlakeNode {
  id: string;
  flake: z.infer<typeof GitHubFlakeSchema> | z.infer<typeof GenericFlakeSchema>;
  outputs: Array<z.infer<typeof FlakeOutputRefSchema>>;
}

export interface FlakeEdge {
  from: string;
  to: string;
  inputName: string;
}

/**
 * Flake Dependency Resolver
 * 
 * Resolves dependencies without file system access
 */
export class FlakeDependencyResolver {
  private graph: FlakeDependencyGraph = {
    nodes: new Map(),
    edges: [],
  };

  /**
   * Add a flake to the dependency graph
   */
  addFlake(
    id: string,
    flake: z.infer<typeof GitHubFlakeSchema> | z.infer<typeof GenericFlakeSchema>,
    outputs: Array<z.infer<typeof FlakeOutputRefSchema>> = []
  ): void {
    this.graph.nodes.set(id, { id, flake, outputs });
  }

  /**
   * Add a dependency edge
   */
  addDependency(from: string, to: string, inputName: string): void {
    this.graph.edges.push({ from, to, inputName });
  }

  /**
   * Generate Nix flake for the dependency graph
   */
  generateFlake(): string {
    const inputs: string[] = [];
    const outputParams: string[] = ['self'];

    // Build inputs from edges
    for (const edge of this.graph.edges) {
      const toNode = this.graph.nodes.get(edge.to);
      if (toNode) {
        const url = FlakeReferenceBuilder.toUrl(toNode.flake);
        inputs.push(`    ${edge.inputName}.url = "${url}";`);
        outputParams.push(edge.inputName);
      }
    }

    return `
{
  description = "Generated composite flake";

  inputs = {
${inputs.join('\n')}
  };

  outputs = { ${outputParams.join(', ')} }: {
    # Composite outputs generated from dependency graph
    packages = self.lib.mkMerge [
      # Merge all package outputs from dependencies
    ];
  };
}
    `.trim();
  }
}

// Type exports
export type GitHubFlake = z.infer<typeof GitHubFlakeSchema>;
export type GenericFlake = z.infer<typeof GenericFlakeSchema>;
export type FlakeOutputRef = z.infer<typeof FlakeOutputRefSchema>;
export type AnyFlake = GitHubFlake | GenericFlake;
