{
  description = "EdgarTools - Python library for SEC EDGAR data extraction";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";
    flake-utils.url = "github:numtide/flake-utils";
  };

  outputs = { self, nixpkgs, flake-utils }:
    flake-utils.lib.eachDefaultSystem (system:
      let
        pkgs = import nixpkgs { inherit system; };

        # edgartoolsãƒ‘ãƒƒã‚±ãƒ¼ã‚¸ã®ãƒ“ãƒ«ãƒ‰é–¢æ•°
        buildEdgartools = pythonPackages: pythonPackages.buildPythonPackage rec {
          pname = "edgartools";
          version = "2.34.0";
          format = "wheel";

          src = pythonPackages.fetchPypi {
            inherit pname version format;
            dist = "py3";
            python = "py3";
            sha256 = "sha256-AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA=";  # è¦: å®Ÿéš›ã®ãƒãƒƒã‚·ãƒ¥å€¤
          };

          propagatedBuildInputs = with pythonPackages; [
            pandas
            lxml
            requests
            beautifulsoup4
            python-dateutil
            pytz
            numpy
            # edgartoolsã®ä¾å­˜é–¢ä¿‚ã‚’ã“ã“ã«è¿½åŠ 
          ];

          # ãƒ†ã‚¹ãƒˆã¯ä¸€æ—¦ç„¡åŠ¹åŒ–ï¼ˆPyPIãƒ‘ãƒƒã‚±ãƒ¼ã‚¸ã«ã¯ãƒ†ã‚¹ãƒˆãŒå«ã¾ã‚Œãªã„ï¼‰
          doCheck = false;

          pythonImportsCheck = [ "edgar" ];

          meta = with pkgs.lib; {
            description = "Python library for SEC EDGAR data extraction and analysis";
            homepage = "https://github.com/dgunning/edgartools";
            license = licenses.mit;
          };
        };
      in
      {
        # overlay: ä»»æ„ã®Pythonãƒãƒ¼ã‚¸ãƒ§ãƒ³ã§ä½¿ç”¨å¯èƒ½ã«ã™ã‚‹
        overlays.default = final: prev: {
          pythonPackagesExtensions = prev.pythonPackagesExtensions ++ [
            (python-final: python-prev: {
              edgartools = buildEdgartools python-final;
            })
          ];
        };

        # ãƒ‡ãƒ•ã‚©ãƒ«ãƒˆãƒ‘ãƒƒã‚±ãƒ¼ã‚¸ï¼ˆPython 3.11ç‰ˆï¼‰
        packages.default = pkgs.python311.pkgs.callPackage buildEdgartools {};

        # è¤‡æ•°ã®Pythonãƒãƒ¼ã‚¸ãƒ§ãƒ³ã§ã®ãƒ‘ãƒƒã‚±ãƒ¼ã‚¸æä¾›ï¼ˆãƒ†ã‚¹ãƒˆç”¨ï¼‰
        packages.edgartools-py39 = pkgs.python39.pkgs.callPackage buildEdgartools {};
        packages.edgartools-py310 = pkgs.python310.pkgs.callPackage buildEdgartools {};
        packages.edgartools-py311 = pkgs.python311.pkgs.callPackage buildEdgartools {};
        packages.edgartools-py312 = pkgs.python312.pkgs.callPackage buildEdgartools {};

        # overlayãƒ†ã‚¹ãƒˆ: å„Pythonãƒãƒ¼ã‚¸ãƒ§ãƒ³ã§importã§ãã‚‹ã“ã¨ã‚’ç¢ºèª
        checks = {
          overlay-py39 = pkgs.runCommand "test-overlay-py39" {
            nativeBuildInputs = [
              (pkgs.python39.withPackages (ps: [ (buildEdgartools ps) ]))
            ];
          } ''
            python -c "import edgar; print('âœ“ Python 3.9 import success')" > $out
          '';

          overlay-py310 = pkgs.runCommand "test-overlay-py310" {
            nativeBuildInputs = [
              (pkgs.python310.withPackages (ps: [ (buildEdgartools ps) ]))
            ];
          } ''
            python -c "import edgar; print('âœ“ Python 3.10 import success')" > $out
          '';

          overlay-py311 = pkgs.runCommand "test-overlay-py311" {
            nativeBuildInputs = [
              (pkgs.python311.withPackages (ps: [ (buildEdgartools ps) ]))
            ];
          } ''
            python -c "import edgar; print('âœ“ Python 3.11 import success')" > $out
          '';

          overlay-py312 = pkgs.runCommand "test-overlay-py312" {
            nativeBuildInputs = [
              (pkgs.python312.withPackages (ps: [ (buildEdgartools ps) ]))
            ];
          } ''
            python -c "import edgar; print('âœ“ Python 3.12 import success')" > $out
          '';
        };

        # é–‹ç™ºã‚·ã‚§ãƒ«
        devShells.default = pkgs.mkShell {
          buildInputs = with pkgs; [
            (python311.withPackages (ps: [
              (buildEdgartools ps)
              ps.ipython
              ps.pytest
            ]))
          ];

          shellHook = ''
            echo "EdgarTools development environment"
            echo "Python version: $(python --version)"
            echo "Try: python -c 'from edgar import Company'"
          '';
        };
      });
}

