{
  description = "Test flake to verify edgartools overlay works correctly";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";
    edgartools.url = "path:../";  # è¦ªãƒ‡ã‚£ãƒ¬ã‚¯ãƒˆãƒªã®edgartools flake
  };

  outputs = { self, nixpkgs, edgartools }:
    let
      system = "x86_64-linux";

      # overlayã‚’é©ç”¨ã—ãŸpkgs
      pkgs = import nixpkgs {
        inherit system;
        overlays = [ edgartools.overlays.${system}.default ];
      };
    in
    {
      # å„Pythonãƒãƒ¼ã‚¸ãƒ§ãƒ³ã§edgartoolsãŒä½¿ãˆã‚‹ã“ã¨ã‚’ç¢ºèª
      packages.${system} = {
        # Python 3.9ç’°å¢ƒ
        test-py39 = pkgs.python39.withPackages (ps: [
          ps.edgartools
        ]);

        # Python 3.10ç’°å¢ƒ
        test-py310 = pkgs.python310.withPackages (ps: [
          ps.edgartools
        ]);

        # Python 3.11ç’°å¢ƒ
        test-py311 = pkgs.python311.withPackages (ps: [
          ps.edgartools
        ]);

        # Python 3.12ç’°å¢ƒ
        test-py312 = pkgs.python312.withPackages (ps: [
          ps.edgartools
        ]);

        # çµ±åˆãƒ†ã‚¹ãƒˆã‚¹ã‚¯ãƒªãƒ—ãƒˆ
        test-all = pkgs.writeShellScriptBin "test-overlay" ''
          echo "Testing edgartools overlay with multiple Python versions..."
          echo ""

          echo "=== Python 3.9 ==="
          ${self.packages.${system}.test-py39}/bin/python -c "
          import sys
          import edgar
          print(f'Python {sys.version_info.major}.{sys.version_info.minor}: âœ“ edgar module imported')
          "

          echo ""
          echo "=== Python 3.10 ==="
          ${self.packages.${system}.test-py310}/bin/python -c "
          import sys
          import edgar
          print(f'Python {sys.version_info.major}.{sys.version_info.minor}: âœ“ edgar module imported')
          "

          echo ""
          echo "=== Python 3.11 ==="
          ${self.packages.${system}.test-py311}/bin/python -c "
          import sys
          import edgar
          print(f'Python {sys.version_info.major}.{sys.version_info.minor}: âœ“ edgar module imported')
          "

          echo ""
          echo "=== Python 3.12 ==="
          ${self.packages.${system}.test-py312}/bin/python -c "
          import sys
          import edgar
          print(f'Python {sys.version_info.major}.{sys.version_info.minor}: âœ“ edgar module imported')
          "

          echo ""
          echo "âœ… All overlay tests passed!"
        '';
      };

      # é–‹ç™ºã‚·ã‚§ãƒ«ï¼ˆãƒ†ã‚¹ãƒˆå®Ÿè¡Œç”¨ï¼‰
      devShells.${system}.default = pkgs.mkShell {
        buildInputs = [
          self.packages.${system}.test-all
        ];

        shellHook = ''
          echo "Overlay test environment ready"
          echo "Run: test-overlay"
        '';
      };
    };
}

