  outputs = { self, nixpkgs, python-flake, edgartools }:
    let
      system = "x86_64-linux";

      # python-flakeã¨edgartoolsã®ä¸¡æ–¹ã®overlayã‚’é©ç”¨
      pkgs = import nixpkgs {
        inherit system;
        overlays = [ 
          edgartools.overlays.${system}.default
        ];
      };
    in
    {
      packages.${system} = {
        # Python 3.12ã§edgartoolsãƒ†ã‚¹ãƒˆ
        test-edgar = pkgs.writeShellScriptBin "test-edgar" ''
          ${pkgs.python312.withPackages (ps: [ ps.edgartools ])}/bin/python << 'EOF'
