except Exception as e:
    print(f"âŒ Error: {e}")
    sys.exit(1)
EOF
        '';
      };

      # ãƒã‚§ãƒƒã‚¯
      checks.${system} = {
        edgartools-import = pkgs.runCommand "test-edgartools-import" {
