{
  description = "Test edgartools overlay";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";
    edgartools.url = "path:..";
  };

  outputs = { self, nixpkgs, edgartools }:
    let
      system = "x86_64-linux";
      pkgs = import nixpkgs {
        inherit system;
        overlays = [ edgartools.overlays.${system}.default ];
      };
    in {
      devShells.${system}.default = pkgs.mkShell {
        packages = [
          (pkgs.python311.withPackages (ps: [ ps.edgartools ]))
        ];

        shellHook = ''
          echo "Testing EdgarTools overlay..."
          python -c "from edgar import Company; print('âœ… EdgarTools works via overlay!')"
        '';
      };

      # Quick test
      packages.${system}.test = pkgs.writeShellScriptBin "test-edgartools" ''
        ${pkgs.python311.withPackages (ps: [ ps.edgartools ])}/bin/python -c "
        from edgar import Company
        print('âœ… EdgarTools successfully imported via overlay')
        "
      '';
    };
}

