{
  description = "EdgarTools - Python library for SEC EDGAR data extraction";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";
    flake-utils.url = "github:numtide/flake-utils";
  };

  outputs = { self, nixpkgs, flake-utils }:
    flake-utils.lib.eachDefaultSystem (system:
      let
        pkgs = import nixpkgs { inherit system; };

        # edgartoolsã®ãƒ“ãƒ«ãƒ‰é–¢æ•°
        buildEdgartools = pythonPackages: pythonPackages.buildPythonPackage rec {
          pname = "edgartools";
          version = "4.8.2";
          format = "wheel";

          src = pythonPackages.fetchPypi {
            inherit pname version format;
            dist = "py3";
            python = "py3";
            sha256 = "sha256-Sqov+QHIzuohrhO3FZaw112IBY1/AX1uYRmGqCHTlRM=";
          };

          propagatedBuildInputs = with pythonPackages; [
            # å¿…é ˆä¾å­˜ï¼ˆPyPI requiresã‹ã‚‰ï¼‰
            beautifulsoup4
            httpx
            httpx-cache
            humanize
            jinja2
            lxml
            nest-asyncio
            orjson
            packaging
            pandas
            pyarrow
            pydantic
            rapidfuzz
            rich
            stamina
            tabulate
            textdistance
            tqdm
            unidecode
          ];

          # ãƒ†ã‚¹ãƒˆã¯PyPIãƒ‘ãƒƒã‚±ãƒ¼ã‚¸ã«ã¯å«ã¾ã‚Œã¦ã„ãªã„
          doCheck = false;

          pythonImportsCheck = [ "edgar" ];

          meta = with pkgs.lib; {
            description = "Python library for SEC EDGAR data extraction and analysis";
            homepage = "https://github.com/dgunning/edgartools";
            license = licenses.mit;
          };
        };
      in
      {
        # overlay: ä»»æ„ã®Pythonãƒãƒ¼ã‚¸ãƒ§ãƒ³ã§åˆ©ç”¨å¯èƒ½
        overlays.default = final: prev: {
          pythonPackagesExtensions = prev.pythonPackagesExtensions ++ [
            (python-final: python-prev: {
              edgartools = buildEdgartools python-final;
            })
          ];
        };

        # ãƒ‡ãƒ•ã‚©ãƒ«ãƒˆãƒ‘ãƒƒã‚±ãƒ¼ã‚¸ï¼ˆPython 3.12ç‰ˆï¼‰
        packages.default = buildEdgartools pkgs.python312Packages;

