        packages.edgartools-py39 = buildEdgartools pkgs.python39Packages;
        packages.edgartools-py310 = buildEdgartools pkgs.python310Packages;
        packages.edgartools-py311 = buildEdgartools pkgs.python311Packages;
        packages.edgartools-py312 = buildEdgartools pkgs.python312Packages;

        # é–‹ç™ºç’°å¢ƒ
        devShells.default = pkgs.mkShell {
          buildInputs = with pkgs; [
