{
  description = "EdgarTools with uv2nix - complete dependency management";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";
    pyproject-nix = {
      url = "github:pyproject-nix/pyproject.nix";
      inputs.nixpkgs.follows = "nixpkgs";
    };
    uv2nix = {
      url = "github:pyproject-nix/uv2nix";
      inputs.pyproject-nix.follows = "pyproject-nix";
      inputs.nixpkgs.follows = "nixpkgs";
    };
  };

  outputs = { self, nixpkgs, uv2nix, pyproject-nix, ... }:
    let
      inherit (nixpkgs) lib;

      # uv2nixã§ãƒ¯ãƒ¼ã‚¯ã‚¹ãƒšãƒ¼ã‚¹ã‚’èª­ã¿è¾¼ã¿
      workspace = uv2nix.lib.workspace.loadWorkspace { workspaceRoot = ./.; };

      # å„ã‚·ã‚¹ãƒ†ãƒ ã®è¨­å®š
      overlay = workspace.mkPyprojectOverlay { sourcePreference = "wheel"; };

      python = nixpkgs.lib.genAttrs [ "x86_64-linux" "aarch64-linux" "x86_64-darwin" "aarch64-darwin" ] (system:
        let
          pkgs = nixpkgs.legacyPackages.${system};
          inherit (pkgs) stdenv;
          pyproject = pyproject-nix.lib.project.loadPyproject { projectRoot = ./.; };

          # Python 3.12ã‚’ä½¿ç”¨
          python = pkgs.python312;
          pythonSet = (pkgs.callPackage pyproject-nix.build.packages {
            inherit python;
          }).overrideScope overlay;

        in {
          inherit pythonSet;

          # edgartoolsãƒ‘ãƒƒã‚±ãƒ¼ã‚¸
          packages.default = pythonSet.edgartools-flake;

          # é–‹ç™ºç’°å¢ƒ
          devShells.default = pkgs.mkShell {
            packages = [
              (python.withPackages (ps: [ pythonSet.edgartools-flake ]))
              pkgs.uv
            ];

            shellHook = ''
              echo "EdgarTools development environment (uv2nix)"
              echo "Python: ${python.version}"
              python -c "from edgar import Company; print('âœ… EdgarTools ready')" 2>/dev/null || echo "âš ï¸  EdgarTools not yet built"
            '';
          };
        });
    in
    lib.foldAttrs lib.mergeAttrs {} (lib.attrValues python) // {
      # ã‚ªãƒ¼ãƒãƒ¼ãƒ¬ã‚¤ã‚’å„ã‚·ã‚¹ãƒ†ãƒ ã«æä¾›
      overlays.default = final: prev: {
        pythonPackagesExtensions = prev.pythonPackagesExtensions ++ [
          overlay
        ];
      };
    };
}

