input.content:
{
  description = "EdgarTools with uv2nix";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";
    uv2nix.url = "github:pyproject-nix/uv2nix";
    pyproject-nix.url = "github:pyproject-nix/pyproject.nix";
  };

  outputs = { nixpkgs, uv2nix, pyproject-nix, ... }:
    let
      inherit (nixpkgs) lib;

      forAllSystems = lib.genAttrs lib.systems.flakeExposed;

      mkPackages = pkgs:
        let
          workspace = uv2nix.lib.workspace.loadWorkspace {
            workspaceRoot = ./.;
          };

          overlay = workspace.mkPyprojectOverlay {
            sourcePreference = "wheel";
          };

          python = pkgs.python312;

          pythonSet = (pkgs.callPackage pyproject-nix.build.packages {
            inherit python;
          }).overrideScope (lib.composeExtensions 
            pyproject-nix.build.overlays.default
            overlay
          );

        in {
          default = python.withPackages (_: [ pythonSet.edgartools ]);
          edgartools = pythonSet.edgartools;
        };

    in {
      packages = forAllSystems (system: 
        mkPackages nixpkgs.legacyPackages.${system}
      );

      devShells = forAllSystems (system:
        let
          pkgs = nixpkgs.legacyPackages.${system};
          packages = mkPackages pkgs;
        in {
          default = pkgs.mkShell {
            packages = [
              packages.default
              pkgs.uv
            ];

            shellHook = ''
              echo "EdgarTools (uv2nix)"
              echo "Python: ${pkgs.python312.version}"
              python -c "from edgar import Company; print('âœ… EdgarTools ready')" 2>/dev/null || true
            '';
          };
        }
      );
    };
}

