{
  description = "EdgarTools - SEC EDGAR data extraction";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";
    uv2nix.url = "github:pyproject-nix/uv2nix";
    pyproject-nix.url = "github:pyproject-nix/pyproject.nix";
  };

  outputs = { self, nixpkgs, uv2nix, pyproject-nix, ... }:
    let
      inherit (nixpkgs) lib;

      forAllSystems = lib.genAttrs [ "x86_64-linux" "aarch64-linux" ];

    in {
      packages = forAllSystems (system:
        let
          pkgs = nixpkgs.legacyPackages.${system};

          workspace = uv2nix.lib.workspace.loadWorkspace {
            workspaceRoot = ./.;
          };

          overlay = workspace.mkPyprojectOverlay {
            sourcePreference = "wheel";
          };

          python = pkgs.python312;

          # pythonSetã‚’ç›´æ¥ä½œæˆ
          pythonSet = python.pkgs.overrideScope (lib.composeExtensions
            (final: prev: {
              # åŸºæœ¬çš„ãªãƒ“ãƒ«ãƒ‰ãƒ„ãƒ¼ãƒ«ã‚’è¿½åŠ 
              setuptools = prev.setuptools or python.pkgs.setuptools;
              wheel = prev.wheel or python.pkgs.wheel;
            })
            overlay
          );

        in {
          default = python.withPackages (_: 
            # uv.lockã‹ã‚‰ã™ã¹ã¦ã®ä¾å­˜é–¢ä¿‚ã‚’å«ã‚ã‚‹
            builtins.attrValues (
              lib.filterAttrs (n: v: 
                # Python ãƒ‘ãƒƒã‚±ãƒ¼ã‚¸ã®ã¿ã‚’é¸æŠ
                lib.isDerivation v && lib.hasAttr "pythonModule" v
              ) pythonSet
            )
          );
        }
      );

      devShells = forAllSystems (system:
        let
          pkgs = nixpkgs.legacyPackages.${system};
        in {
          default = pkgs.mkShell {
            buildInputs = [
              self.packages.${system}.default
              pkgs.uv
            ];

            shellHook = ''
              echo "EdgarTools with uv2nix"
              echo "Python: ${pkgs.python312.version}"
              echo "Testing import..."
              python -c "from edgar import Company; print('âœ… EdgarTools imported successfully')" || echo "âŒ Import failed"
            '';
          };
        }
      );
    };
}

