    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";
    flake-utils.url = "github:numtide/flake-utils";
  };

  outputs = { self, nixpkgs, flake-utils }:
    flake-utils.lib.eachDefaultSystem (system:
      let
        pkgs = nixpkgs.legacyPackages.${system};
        python = pkgs.python312;

      in {
        devShells.default = pkgs.mkShell {
          buildInputs = with pkgs; [
            python312
            uv
          ];

          shellHook = ''
            echo "EdgarTools development environment"
            echo "Python: ${python.version}"

            # Create virtual environment if not exists
            if [ ! -d .venv ]; then
              echo "Creating virtual environment..."
              uv venv --python ${python}/bin/python
            fi

            # Activate venv
            source .venv/bin/activate

            # Install dependencies
            if ! python -c "import edgar" 2>/dev/null; then
              echo "Installing EdgarTools..."
              uv pip install edgartools==4.8.2
            fi

            # Test import
            python -c "from edgar import Company; print('âœ… EdgarTools ready')" || echo "âŒ Import failed"
          '';
        };
      }
    );
}

