          pandas
          pydantic
          pytest
          pytest-asyncio
        ]);

        # kuzu_pyモジュールのパッケージ化
        kuzu-py-module = pkgs.python312.pkgs.buildPythonPackage {
          pname = "kuzu-py";
          version = "0.1.0";
          src = ./.;

          propagatedBuildInputs = with pkgs.python312Packages; [
            customKuzu
            numpy
            pandas
            pydantic
          ];

          # テストは別途実行
          doCheck = false;

          # PYTHONPATHの設定
          pythonImportsCheck = [ "core.database" ];
        };
      in
      {
        # 開発環境
        devShells.default = pkgs.mkShell {
          buildInputs = [ pythonEnv ];
          shellHook = ''
            export PYTHONPATH="$PWD:$PYTHONPATH"
            echo "KuzuDB Python development environment"
            echo "Python: ${pythonEnv}/bin/python"
          '';
        };

        # パッケージ出力
        packages = {
          default = kuzu-py-module;
          kuzu = customKuzu;
          pythonEnv = pythonEnv;
          module = kuzu-py-module;
        };

        # テストランナー
        apps.test = {
          type = "app";
          program = "${pkgs.writeShellScriptBin "test" ''
            export PYTHONPATH="${./.}:$PYTHONPATH"
            cd ${../}
            ${pythonEnv}/bin/python test_minimal_kuzu.py
          ''}/bin/test";
        };
      });
}

