#!/usr/bin/env bash

# æœ€å°é™ã®SQLiteâ†’KuzuDBé€£æºç¢ºèªï¼ˆBunç‰ˆï¼‰
# KuzuDB TSãƒ©ã‚¤ãƒ–ãƒ©ãƒªã‚’ä½¿ç”¨ã—ãŸã‚¢ã‚¿ãƒƒãƒå‹•ä½œç¢ºèª

set -euo pipefail

# ä½œæ¥­ãƒ‡ã‚£ãƒ¬ã‚¯ãƒˆãƒª
WORK_DIR="./data_bun"
SQLITE_DB="$WORK_DIR/test.db"

# ãƒ‡ã‚£ãƒ¬ã‚¯ãƒˆãƒªä½œæˆ
mkdir -p "$WORK_DIR"

echo "=== æœ€å°é™ã®SQLiteâ†’KuzuDBé€£æºãƒ†ã‚¹ãƒˆ (Bunç‰ˆ) ==="
echo ""

# 1. SQLiteãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹ä½œæˆ
echo "1. SQLiteãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹ä½œæˆ..."
cat <<EOF | nix shell nixpkgs#sqlite -c sqlite3 "$SQLITE_DB"
CREATE TABLE users (id INTEGER PRIMARY KEY, name TEXT);
INSERT INTO users (id, name) VALUES (1, 'Alice'), (2, 'Bob');
EOF
echo "âœ… SQLiteãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹ä½œæˆå®Œäº†"

# 2. Bunã‚¹ã‚¯ãƒªãƒ—ãƒˆä½œæˆ
echo ""
echo "2. Bunã‚¹ã‚¯ãƒªãƒ—ãƒˆä½œæˆ..."
cat > "$WORK_DIR/attach_test.ts" <<'EOF'
// SQLiteã‚’KuzuDBã«ã‚¢ã‚¿ãƒƒãƒã™ã‚‹æœ€å°ä¾‹
import { Database, Connection } from "kuzu";

async function main() {
  console.log("KuzuDBæ¥ç¶šé–‹å§‹...");

  try {
    // KuzuDBãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹ä½œæˆ
    const db = new Database("./kuzu_db");
    const conn = new Connection(db);

    // SQLiteæ‹¡å¼µã‚’ã‚¤ãƒ³ã‚¹ãƒˆãƒ¼ãƒ«ãƒ»ãƒ­ãƒ¼ãƒ‰
    await conn.query("INSTALL sqlite;");
    await conn.query("LOAD sqlite;");
    console.log("âœ… SQLiteæ‹¡å¼µãƒ­ãƒ¼ãƒ‰å®Œäº†");

    // SQLiteãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹ã‚’ã‚¢ã‚¿ãƒƒãƒ
    await conn.query("ATTACH './test.db' AS sqlitedb (dbtype sqlite);");
    console.log("âœ… SQLiteãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹ã‚¢ã‚¿ãƒƒãƒå®Œäº†");

    // ãƒ‡ãƒ¼ã‚¿ç¢ºèª
    const result = await conn.query("LOAD FROM sqlitedb.users RETURN *;");
    console.log("âœ… ãƒ‡ãƒ¼ã‚¿èª­ã¿è¾¼ã¿æˆåŠŸ:");

    // çµæœè¡¨ç¤º
    while (result.hasNext()) {
      const row = result.getNext();
      console.log(`  ID: ${row.id}, Name: ${row.name}`);
    }

    // ã‚¯ãƒªãƒ¼ãƒ³ã‚¢ãƒƒãƒ—
    await conn.query("DETACH sqlitedb;");
    conn.close();

    console.log("âœ… ãƒ†ã‚¹ãƒˆå®Œäº†");
  } catch (error) {
    console.error("âŒ ã‚¨ãƒ©ãƒ¼:", error);
    process.exit(1);
  }
}

main();
EOF
echo "âœ… Bunã‚¹ã‚¯ãƒªãƒ—ãƒˆä½œæˆå®Œäº†"

# 3. Bunã§å®Ÿè¡Œ
echo ""
echo "3. Bunã‚¹ã‚¯ãƒªãƒ—ãƒˆå®Ÿè¡Œ..."
cd "$WORK_DIR"
bun run attach_test.ts

echo ""
echo "=== æœ€å°é™ãƒ†ã‚¹ãƒˆå®Œäº† ==="

