#!/usr/bin/env bun

// ç°¡å˜ãªSQLiteâ†’KuzuDBã‚¢ã‚¿ãƒƒãƒä¾‹ï¼ˆç›´æ¥å®Ÿè¡Œå¯èƒ½ï¼‰

import { createDatabase, createConnection } from "../../persistence/kuzu_ts/bun/mod.ts";

async function main() {
  console.log("=== SQLiteâ†’KuzuDBç°¡æ˜“ã‚¢ã‚¿ãƒƒãƒãƒ†ã‚¹ãƒˆ ===\n");

  // SQLiteãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹æº–å‚™
  const sqliteDb = "./data_bun/simple.db";
  console.log("1. SQLiteãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹ä½œæˆ...");

  // Bunã®çµ„ã¿è¾¼ã¿SQLite APIã‚’ä½¿ç”¨
  const { Database: SQLiteDB } = await import("bun:sqlite");
  const sqlite = new SQLiteDB(sqliteDb, { create: true });

  // ãƒ†ãƒ¼ãƒ–ãƒ«ä½œæˆã¨ãƒ‡ãƒ¼ã‚¿æŠ•å…¥
  sqlite.run(`
    CREATE TABLE IF NOT EXISTS products (
      id INTEGER PRIMARY KEY,
      name TEXT,
      price REAL
    )
  `);

  sqlite.run(`
    INSERT OR REPLACE INTO products (id, name, price) VALUES
    (1, 'Laptop', 999.99),
    (2, 'Mouse', 29.99),
    (3, 'Keyboard', 79.99)
  `);

  console.log("âœ… SQLiteãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹æº–å‚™å®Œäº†\n");

  // KuzuDBã§ã‚¢ã‚¿ãƒƒãƒ
  console.log("2. KuzuDBã§ã‚¢ã‚¿ãƒƒãƒ...");

  const dbResult = await createDatabase("./data_bun/kuzu_simple");
  if (!dbResult.success) {
    console.error("âŒ KuzuDBãƒ‡ãƒ¼ã‚¿ãƒ™ãƒ¼ã‚¹ä½œæˆå¤±æ•—:", dbResult.error);
    process.exit(1);
  }

  const connResult = await createConnection(dbResult.value);
  if (!connResult.success) {
    console.error("âŒ KuzuDBæ¥ç¶šä½œæˆå¤±æ•—:", connResult.error);
    process.exit(1);
  }

  const conn = connResult.value;

  try {
    // SQLiteæ‹¡å¼µã®ãƒ­ãƒ¼ãƒ‰
    await conn.query("INSTALL sqlite;");
    await conn.query("LOAD sqlite;");
    console.log("âœ… SQLiteæ‹¡å¼µãƒ­ãƒ¼ãƒ‰å®Œäº†\n");

