// KuzuDB要件管理システム Search Service完全統合スキーマ v3.4
// 作成日: 2025-07-13
// 目的: Search serviceのスキーマに完全準拠したスキーマ

// ========================================
// ノードテーブル（エンティティ）- 3個
// ========================================

// 要件エンティティ（Search Service対応版）
CREATE NODE TABLE RequirementEntity (
    id STRING PRIMARY KEY,
    title STRING,
    description STRING,            -- メインコンテンツフィールド（search serviceで使用）
    embedding DOUBLE[256],         -- search service必須フィールド
    status STRING DEFAULT 'proposed'
);

// ロケーションURI（変更なし）
CREATE NODE TABLE LocationURI (
    id STRING PRIMARY KEY
);

// バージョン状態（簡素化版）
CREATE NODE TABLE VersionState (
    id STRING PRIMARY KEY,
    timestamp STRING,
    description STRING,
    change_reason STRING,
    operation STRING DEFAULT 'UPDATE',
    author STRING DEFAULT 'system'
);

// ========================================
// エッジテーブル（関係）- 4個
// ========================================

// LOCATES関係
CREATE REL TABLE LOCATES (
    FROM LocationURI TO RequirementEntity,
    entity_type STRING DEFAULT 'requirement',
    current BOOLEAN DEFAULT false
);

// バージョン管理
CREATE REL TABLE TRACKS_STATE_OF (
    FROM VersionState TO LocationURI,
    entity_type STRING
);

// 階層関係
CREATE REL TABLE CONTAINS_LOCATION (
    FROM LocationURI TO LocationURI
);

// 依存関係
CREATE REL TABLE DEPENDS_ON (
    FROM RequirementEntity TO RequirementEntity,
    dependency_type STRING DEFAULT 'requires',
    reason STRING
);

// URI部分一致による要件検索（バッチ設計）
// 入力: {uri_contains: ["auth", "login"]} - 検索文字列の配列
// 
// 規約準拠:
// - security.md: CONTAINSを使った安全な部分一致検索
// - バッチ設計原則: 1件でも配列で処理

WITH $uri_contains AS search_terms
UNWIND search_terms AS term
WITH DISTINCT term
WHERE term <> ''  // 空文字列を除外

// LocationURIを検索（大文字小文字を区別しない）
MATCH (l:LocationURI)
WHERE toLower(l.id) CONTAINS toLower(term)

// 関連する要件を取得
MATCH (l)-[:LOCATES]->(r:RequirementEntity)

// 結果を収集
WITH r, l, collect(DISTINCT term) AS matched_terms
RETURN r.id AS id,
       r.title AS title,
       r.description AS description,
       r.status AS status,
       l.id AS uri,
       matched_terms,
       size(matched_terms) AS match_count
ORDER BY match_count DESC, r.id

