     1→{
     2→  description = "KuzuDB thin wrapper for Python";
     3→
     4→  inputs = {
     5→    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";
     6→    flake-utils.url = "github:numtide/flake-utils";
     7→  };
     8→
     9→  outputs = { self, nixpkgs, flake-utils }:
    10→    flake-utils.lib.eachDefaultSystem (system:
    11→      let
    12→        pkgs = nixpkgs.legacyPackages.${system};
    13→        
    14→        # カスタマイズ可能なkuzuパッケージ
    15→        customKuzu = pkgs.python312Packages.kuzu.overrideAttrs (oldAttrs: {
    16→          # 拡張機能をここで追加可能
    17→        });
    18→        
    19→        # kuzu_py パッケージ  
    20→        kuzuPy = pkgs.python312Packages.buildPythonPackage rec {
    21→          pname = "kuzu_py";
    22→          version = "0.1.0";
    23→          
    24→          src = pkgs.lib.cleanSourceWith {
    25→            src = ./.;
    26→            filter = path: type: 
    27→              (pkgs.lib.hasSuffix ".py" path) ||
    28→              (pkgs.lib.hasSuffix "pyproject.toml" path) ||
    29→              (pkgs.lib.hasSuffix "setup.py" path);
    30→          };
    31→          
    32→          # setuptools形式でビルド
    33→          pyproject = true;
    34→          build-system = with pkgs.python312Packages; [
    35→            setuptools
    36→            wheel
    37→          ];
    38→          
    39→          propagatedBuildInputs = with pkgs.python312Packages; [
    40→            customKuzu
    41→          ];
    42→          
    43→          pythonImportsCheck = [ "kuzu_py" ];
    44→          doCheck = false;
    45→        };
    46→        
    47→        # Python環境
    48→        pythonEnv = pkgs.python312.withPackages (ps: with ps; [
    49→          kuzuPy
    50→          pytest
    51→        ]);
    52→      in
    53→      {
    54→        # 開発環境
    55→        devShells.default = pkgs.mkShell {
    56→          buildInputs = with pkgs; [
    57→            pythonEnv
    58→          ];
    59→          
    60→          shellHook = ''
    61→            echo "KuzuDB thin wrapper"
    62→            echo "Python version: $(python --version)"
    63→          '';
    64→        };
    65→        
    66→        # パッケージ出力
    67→        packages = {
    68→          default = pythonEnv;
    69→          kuzu = customKuzu;
    70→          pythonEnv = pythonEnv;
    71→          kuzuPy = kuzuPy;
    72→        };
    73→        
    74→        # テストランナー
    75→        apps = {
    76→          test = {
    77→            type = "app";
    78→            program = "${pkgs.writeShellScriptBin "test" ''
    79→              cd /home/nixos/bin/src/persistence/kuzu_py
    80→              ${pythonEnv}/bin/pytest -v test_kuzu_py.py
    81→            ''}/bin/test";
    82→          };
    83→          
    84→          # e2eテストランナー
    85→          e2e = {
    86→            type = "app";
    87→            program = "${pkgs.writeShellScriptBin "e2e" ''
    88→              cd /home/nixos/bin/src/persistence/kuzu_py
    89→              echo "Running e2e tests for external import capability..."
    90→              ${pythonEnv}/bin/pytest -v test_e2e.py
    91→            ''}/bin/e2e";
    92→          };
