#!/usr/bin/env python3
"""
@vessel
@category: transform
@tags: json,filter,extract
@input: json
@output: json
@description: JSONから指定フィールドを抽出
@examples:
  - input: {"users": [{"name": "Alice"}]}
    args: .users[0].name
    output: "Alice"
"""
import sys
import json

