#!/usr/bin/env bun
/**
 * Vessel for Bun - Dynamic TypeScript execution container
 * Reads TypeScript/JavaScript from stdin and executes it
 */

// Read script from stdin
const script = await Bun.stdin.text();

// Execution context
const context = {
  vessel: true,
  console,
  process,
  Bun,
  Buffer,
  fetch,
  // Bun-specific features
  $: Bun.$,  // Shell command execution
};

try {
  // Create function with context variables
  const AsyncFunction = Object.getPrototypeOf(async function(){}).constructor;
  const func = new AsyncFunction(
    ...Object.keys(context),
    script
  );

  // Execute with context
  await func(...Object.values(context));
} catch (error) {
  console.error("Error executing script:", error);
  process.exit(1);
}

