/**
 * Vessel for Bun - Dynamic script execution container
 * For TypeScript support, use .ts files or transpile first
 */

const script = await Bun.stdin.text();

// Simple approach - eval for JavaScript only
// For TypeScript, use Bun's file-based execution
try {
  // Check if it looks like TypeScript
  if (script.includes(': ') || script.includes('interface ') || script.includes('<')) {
    console.error("TypeScript detected. Please use vessel_ts.ts for TypeScript support.");
    process.exit(1);
  }

  // For JavaScript, use eval
  await eval(script);
} catch (error) {
  console.error("Error executing script:", error);
  process.exit(1);
}

