#!/usr/bin/env bun
/**
 * Data-aware Vessel for Bun - Receives data from stdin
 * Usage: echo "data" | bun vessel_data.ts 'console.log(data)'
 */

// Get script from command line argument
const script = process.argv[2];
if (!script) {
  console.error("Usage: bun vessel_data.ts 'script'");
  process.exit(1);
}

// Read data from stdin
const data = await Bun.stdin.text();

// Execution context with data
const context = {
  vessel: true,
  data: data.trim(),
  console,
  process,
  Bun,
  Buffer,
  fetch,
  JSON,
  $: Bun.$,
};

try {
  // Use eval with proper context binding
  const contextEntries = Object.entries(context);
  const contextKeys = contextEntries.map(([key]) => key);
  const contextValues = contextEntries.map(([, value]) => value);

  // Wrap in async function
  const wrappedScript = `
    (async () => {
      ${script}
    })()
  `;

  // Create function with all context variables
  const func = new Function(...contextKeys, `return ${wrappedScript}`);

  // Execute and await result
  await func(...contextValues);
} catch (error) {
  console.error("Error executing script:", error);
  process.exit(1);
}

