{
  description = "@lazarv/react-server with Node.js 22";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";
    flake-utils.url = "github:numtide/flake-utils";
  };

  outputs = { self, nixpkgs, flake-utils }:
    flake-utils.lib.eachDefaultSystem (system:
      let
        pkgs = nixpkgs.legacyPackages.${system};
      in
      {
        devShells.default = pkgs.mkShell {
          buildInputs = with pkgs; [
            nodejs_22
            pnpm
            # Cloudflare Workers CLI
            nodePackages.wrangler
          ];

          shellHook = ''
            echo "=ï¿½ @lazarv/react-server ï¿½ Node 22 ï¿½zï¿½ï¿½"
            echo ""
            echo "Node.js: $(node --version)"
            echo "pnpm: $(pnpm --version)"
            echo ""
            echo "(ï¿½ï¿½jï¿½ï¿½ï¿½ï¿½:"
            echo "  pnpm install  - ï¿½Xï¿½ï¿½nï¿½ï¿½ï¿½ï¿½ï¿½"
            echo "  pnpm dev      - ï¿½zï¿½ï¿½ï¿½ï¿½wï¿½"
            echo "  pnpm build    - ï¿½ï¿½ï¿½ï¿½ï¿½ï¿½ï¿½ï¿½ï¿½ï¿½"
            echo "  wrangler      - Cloudflare Workers CLI"
            echo ""
          '';
        };
      }
    );
}

