{
  description = "Waku application using waku_node base";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";
    flake-utils.url = "github:numtide/flake-utils";
    waku-base = {
      url = "path:../../poc/vite_rsc/waku_node";
