        # App-specific configuration
        apps = {
          build = {
            type = "app";
            program = "${waku-base.packages.${system}.build}/bin/waku-build";
          };
          deploy = {
            type = "app";
            program = "${waku-base.packages.${system}.deploy}/bin/waku-deploy";
          };
        };
      });
}

