  scripts = {
    dev = "waku dev";
    build = "waku build";
    start = "waku start";
  };
  dependencies = {
    react = "19.0.0-rc-bf7e210c-20241017";
    "react-dom" = "19.0.0-rc-bf7e210c-20241017";
    waku = "0.21.2";
