#!/usr/bin/env nix-shell
#! nix-shell -i bash -p fzf

# Search through bash keybindings using fzf
# Combines output from 'bind -X' and 'bind -p' for comprehensive key mapping search

{
    echo "----- bind -X -----"
    bind -X
    echo "----- bind -p -----"
    bind -p
} | fzf --preview 'echo {}'
