/**
 * Outputs a greeting message to standard output
 * @returns Result indicating success or failure of the operation
 * @example
 * const result = hello();
 * if (result.ok) {
 *   console.log("Greeting completed successfully");
 * } else {
 *   console.error("Failed to greet:", result.error.message);
 * }
 */
export function hello(): { ok: true } | { ok: false; error: Error } {
  try {
    console.log("Hello from org_test POC!");
    return { ok: true };
  } catch (error) {
    return { 
      ok: false, 
      error: error instanceof Error ? error : new Error(String(error))
    };
  }
}

if (import.meta.main) {
  const result = hello();
  if (!result.ok) {
    console.error("Error:", result.error.message);
    Deno.exit(1);
  }
}

