     1→// Data Transformer - Handles schema transformations
     2→export class Transformer {
     3→  private transforms = new Map<string, any>();
     4→  
     5→  async registerTransform(data: {
     6→    from: string;
     7→    to: string;
     8→    script?: string;
     9→    reverseScript?: string;
    10→  }) {
    11→    const key = `${data.from}->${data.to}`;
    12→    this.transforms.set(key, {
    13→      forward: data.script,
    14→      reverse: data.reverseScript
    15→    });
    16→  }
    17→  
    18→  async transform(data: any, from: string, to: string, direction: "forward" | "reverse" = "forward") {
    19→    const key = `${from}->${to}`;
    20→    const transform = this.transforms.get(key);
    21→    
    22→    if (!transform) {
    23→      // Use default mapping
    24→      return this.defaultTransform(data, direction);
    25→    }
    26→    
    27→    const script = direction === "forward" ? transform.forward : transform.reverse;
    28→    if (!script) {
    29→      return this.defaultTransform(data, direction);
    30→    }
    31→    
    32→    // Execute in Worker for isolation
    33→    return await this.executeInWorker(script, data);
    34→  }
    35→  
    36→  private async executeInWorker(script: string, data: any): Promise<any> {
    37→    const workerCode = `
    38→      ${script}
    39→      self.onmessage = (e) => {
    40→        try {
    41→          const result = transform(e.data);
    42→          self.postMessage({ success: true, result });
    43→        } catch (error) {
    44→          self.postMessage({ success: false, error: error.message });
    45→        }
    46→      };
    47→    `;
    48→    
    49→    const blob = new Blob([workerCode], { type: "application/javascript" });
    50→    const worker = new Worker(URL.createObjectURL(blob), {
    51→      type: "module",
    52→      // @ts-ignore - Deno specific
    53→      deno: {
    54→        permissions: "none"  // No permissions - complete isolation
    55→      }
    56→    });
    57→    
    58→    return new Promise((resolve, reject) => {
    59→      worker.onmessage = (e) => {
    60→        worker.terminate();
    61→        if (e.data.success) {
    62→          resolve(e.data.result);
    63→        } else {
    64→          reject(new Error(e.data.error));
    65→        }
