      let
        pkgs = nixpkgs.legacyPackages.${system};
      in
      {
        apps = rec {
          # デフォルトアプリで動的にアプリ一覧を表示
          default = {
            type = "app";
            program = let
              # ビルド時に利用可能なアプリ名を取得
