                このプロジェクトで利用可能なコマンド:

                ${builtins.concatStringsSep "\n" (map (name: "  nix run .#${name}") appNames)}

                使用例:
                  echo '{"operation": "example"}' | nix run .#run
                  nix run .#test
              '';
            in "${pkgs.writeShellScript "show-help" ''
              cat << 'EOF'
              ${helpText}
              EOF
            ''}";
          };

          # 実際のアプリケーション
          run = {
            type = "app";
            program = "${pkgs.writeShellScript "run" ''
              echo "Running with input: $(cat)"
            ''}";
