# NixOS systemd timer example - Hello World every 30 seconds
# 
# 使い方:
# 1. このファイルを /etc/nixos/configuration.nix にインポート
#    imports = [ ./hello-timer-example.nix ];
# 
# 2. または直接 configuration.nix に内容をコピー
#
# 3. sudo nixos-rebuild switch で適用
#
# 4. 動作確認コマンド:
#    systemctl status hello-world.timer
#    systemctl status hello-world.service
#    journalctl -u hello-world -f

{ config, pkgs, ... }:

{
  # systemd サービスの定義
  systemd.services.hello-world = {
    description = "Hello World Service";
    serviceConfig = {
      Type = "oneshot";
      ExecStart = "${pkgs.bash}/bin/bash -c 'echo \"[$(date)] Hello World from systemd timer!\" | ${pkgs.systemd}/bin/systemd-cat -t hello-world'";
    };
  };

  # systemd タイマーの定義
  systemd.timers.hello-world = {
    description = "Hello World Timer - runs every 30 seconds";
    wantedBy = [ "timers.target" ];
    timerConfig = {
      # 初回実行: システム起動10秒後
      OnBootSec = "10s";
      # 繰り返し: 30秒毎
      OnUnitActiveSec = "30s";
      # 精度: 1秒（省電力より正確性を優先）
      AccuracySec = "1s";
      # 永続化: システム停止中の実行をスキップ
      Persistent = false;
    };
  };
}

