    tmux = {
        enable = true;
    };

  };

  home.packages = with pkgs; [
    unzip
    tre-command
    fzf
    ripgrep
    fd
    bat
    zoxide
    eza
    broot

    pnpm
    nodejs_22
    uv
    python311

    # aider-chat
  ] ++ (import ./language.nix { inherit pkgs; });

  home.file.".profile".text = ''
    source ~/.profile_
  '';
