input.content:
{
  description = "analyze_jsonl - DuckDBを使用した分散JSONLファイル分析ツール";

  inputs = {
    nixpkgs.url = "github:NixOS/nixpkgs/nixos-unstable";
    flake-utils.url = "github:numtide/flake-utils";
  };

  outputs = { self, nixpkgs, flake-utils }:
    flake-utils.lib.eachDefaultSystem (system:
      let
        pkgs = nixpkgs.legacyPackages.${system};

        pythonEnv = pkgs.python311.withPackages (ps: with ps; [
          duckdb
          pytest
        ]);

        analyze-jsonl = pkgs.writeScriptBin "analyze-jsonl" ''
          #!${pythonEnv}/bin/python
          import sys
          sys.path.insert(0, "${./.}")
          from core import main
          if __name__ == "__main__":
              main()
        '';

        test-runner = pkgs.writeScriptBin "test-analyze-jsonl" ''
          #!${pkgs.bash}/bin/bash
          cd ${./.}
          export PYTHONPATH="${./.}:$PYTHONPATH"
          ${pythonEnv}/bin/pytest -v "$@"
        '';

      in
      {
        # デフォルトパッケージ（nix run）
        packages.default = analyze-jsonl;

        # 追加パッケージ
        packages.test = test-runner;
        packages.analyze = analyze-jsonl;

        # 開発シェル（nix develop）
        devShells.default = pkgs.mkShell {
          buildInputs = with pkgs; [
            pythonEnv
          ];

          shellHook = ''
            echo "analyze_jsonl開発環境"
            echo ""
            echo "使用可能なコマンド:"
            echo "  nix run          - analyze_jsonlを実行"
            echo "  nix run .#test   - テストを実行"
            echo "  pytest           - 開発シェル内でテスト実行"
            echo ""
            export PYTHONPATH="$PWD:$PYTHONPATH"
          '';
        };

        # アプリケーション定義（nix run .#<name>）
        apps = {
          default = flake-utils.lib.mkApp {
            drv = analyze-jsonl;
          };

          test = flake-utils.lib.mkApp {
            drv = test-runner;
          };

          # 単一ファイルのテスト用
          test-core = {
            type = "app";
            program = "${pkgs.writeScript "test-core" ''
              #!${pkgs.bash}/bin/bash
              cd ${./.}
              export PYTHONPATH="${./.}:$PYTHONPATH"
              ${pythonEnv}/bin/pytest -v core.py
            ''}";
          };

          test-adapters = {
            type = "app";
            program = "${pkgs.writeScript "test-adapters" ''
              #!${pkgs.bash}/bin/bash
              cd ${./.}
              export PYTHONPATH="${./.}:$PYTHONPATH"
              ${pythonEnv}/bin/pytest -v adapters.py
            ''}";
          };
        };
      });
}

