538	                  return `データ取得成功: ${JSON.stringify(result.data)}`;
539	              } else if ("field" in result && "message" in result && result.status === "validation_error") {
540	                  return `バリデーションエラー [${result.field}]: ${result.message}`;
541	              } else if ("code" in result && "message" in result && result.status === "execution_error") {
542	                  return `実行エラー [${result.code}]: ${result.message}`;
543	              } else {
544	                  return "未知のエラー";
545	              }
546	          }
