588	              error: (message: string, error: any) => void;
589	            };
590	          }
591	          
592	          // 高階関数による依存性注入
593	          function createQueryService(deps: QueryDependencies) {
594	            return function executeQuery(query: string, params: any): QueryResult {
595	              if (!deps.repository.validate(query)) {
596	                deps.logger.error("Invalid query", { query });
597	                return { code: "INVALID_QUERY", message: "Query validation failed" };
598	              }
599	              
600	              try {
601	                const result = deps.repository.execute(query, params);
602	                deps.logger.info("Query executed successfully");
603	                return { data: result };
604	              } catch (error) {
