  private hashSchema(schema: any): string {
    return JSON.stringify(schema); // Simple hash for now
  }

  private generateDefaultTransform() {
    // Simple field mapping for now
    return {
      output: {
        "location": "city",
        "city": "location"
      },
      input: {
        "temperature": "temp",
        "humidity": "humid",
        "location": "city",
        "temp": "temperature",
        "humid": "humidity",
        "city": "location"
      }
    };
  }

  private async loadSchemaFromPaths(inputPath: string, outputPath: string) {
    try {
      // Convert relative paths to absolute
      const resolvedInputPath = this.resolvePath(inputPath);
      const resolvedOutputPath = this.resolvePath(outputPath);

      // Read files
      let inputContent, outputContent;
      try {
        inputContent = await Deno.readTextFile(resolvedInputPath);
      } catch (error) {
        if (error instanceof Deno.errors.NotFound) {
          throw new Error(`Schema file not found: ${resolvedInputPath}`);
        }
        throw error;
      }

      try {
        outputContent = await Deno.readTextFile(resolvedOutputPath);
      } catch (error) {
        if (error instanceof Deno.errors.NotFound) {
          throw new Error(`Schema file not found: ${resolvedOutputPath}`);
        }
        throw error;
      }

      // Parse JSON
      let inputSchema, outputSchema;
      try {
        inputSchema = JSON.parse(inputContent);
      } catch {
        throw new Error(`Invalid JSON in file: ${resolvedInputPath}`);
      }

      try {
        outputSchema = JSON.parse(outputContent);
      } catch {
        throw new Error(`Invalid JSON in file: ${resolvedOutputPath}`);
      }

      // Validate JSON Schema format
      this.validateJsonSchema(inputSchema, resolvedInputPath);
      this.validateJsonSchema(outputSchema, resolvedOutputPath);

      return {
        input: inputSchema,
        output: outputSchema
      };
    } catch (error) {
      // Re-throw with appropriate message
      throw error;
    }
  }

  private resolvePath(path: string): string {
    // Handle relative paths
    if (path.startsWith("./") || path.startsWith("../")) {
      return new URL(path, `file://${Deno.cwd()}/`).pathname;
    }
    return path;
  }

  private validateJsonSchema(schema: any, path: string): void {
    // Basic JSON Schema validation
    if (typeof schema !== "object" || schema === null) {
      throw new Error(`Invalid JSON Schema in ${path}: must be an object`);
    }

    // Check for type or properties (basic schema structure)
    if (!schema.type && !schema.properties && !schema.$ref && !schema.allOf && !schema.anyOf && !schema.oneOf) {
      throw new Error(`Invalid JSON Schema in ${path}: missing type or properties`);
    }
  }
}

