        },
        id: 8
      })
    });

    const result = await response.json();
    assertEquals(result.error.code, -32603); // Internal error
    assert(result.error.message.includes("Invalid JSON Schema"));
  });

  } finally {
    // クリーンアップ
    await Deno.remove(testDir, { recursive: true });
    controller.abort();
    await server;
  }
});

