        return {
          jsonrpc: "2.0",
          error: {
            ...RPC_ERRORS.INVALID_SCHEMA,
            data: { detail: error.message }
          },
          id: request.id || null
        };
      }

      if (error.message === "No available providers") {
        return {
          jsonrpc: "2.0",
          error: {
            ...RPC_ERRORS.NO_PROVIDER,
            data: { consumer: request.params?.from }
          },
          id: request.id || null
        };
      }

      if (error.message.includes("Missing required parameter") || error.message.includes("Invalid parameter type")) {
        return {
          jsonrpc: "2.0",
          error: {
            code: -32602,
            message: "Invalid params",
            data: { detail: error.message }
          },
          id: request.id || null
        };
      }

      if (error.message.includes("Schema file not found")) {
        return {
          jsonrpc: "2.0",
          error: {
            code: -32603,
            message: "Internal error",
            data: { detail: error.message }
          },
          id: request.id || null
        };
      }

      if (error.message.includes("Invalid JSON")) {
        return {
          jsonrpc: "2.0",
          error: {
            code: -32603,
            message: "Internal error",
            data: { detail: error.message }
          },
          id: request.id || null
        };
      }

      if (error.message.includes("Invalid JSON Schema")) {
        return {
          jsonrpc: "2.0",
          error: {
            code: -32603,
            message: "Internal error",
            data: { detail: error.message }
          },
          id: request.id || null
        };
      }

      if (error.message.includes("Invalid registration type")) {
        return {
          jsonrpc: "2.0",
          error: {
            code: -32602,
            message: "Invalid params",
            data: { detail: error.message }
          },
          id: request.id || null
        };
      }
    }

    return {
      jsonrpc: "2.0",
      error: RPC_ERRORS.INTERNAL_ERROR,
      id: request.id || null
    };
  }
}

// Server handler function
export async function handler(req: Request): Promise<Response> {
  const url = new URL(req.url);

  // JSON-RPC2 endpoint
  if (url.pathname === "/rpc" && req.method === "POST") {
    let body;
    try {
      body = await req.json();
    } catch {
      return Response.json({
        jsonrpc: "2.0",
        error: RPC_ERRORS.PARSE_ERROR,
        id: null
      });
    }

    // Handle batch requests
    if (Array.isArray(body)) {
      const responses = await Promise.all(
        body.map(request => handleRpcRequest(request))
      );
      return Response.json(responses);
    }

    // Handle single request
    const response = await handleRpcRequest(body);
    return Response.json(response);
  }

  return new Response("Not Found", { status: 404 });
}

// Start server if run directly
if (import.meta.main) {
  console.log("Contract Service starting on http://localhost:8000");
  serve(handler, { port: 8000 });
}

